	grid-column: 1/-1;
}
.navigations {
	display: flex;
	color: #000000;
	flex-direction: column;
	align-items: center;
}
.navigation {
	padding-top: 0.5rem;
	padding-bottom: 0.5rem;
	text-align: center;
	width: 100%;
}
.navigation:hover {
	color: #4aadb5;
}
.buttonWrapper {
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
}
