	color: #ffffff;
	font-weight: 700;
}
@media (min-width: 1024px) {
	.header {
		margin-left: auto;
		margin-right: auto;
		max-width: 1536px;
		grid-template-columns: repeat(7, minmax(0, 1fr));
	}
	.logo {
		max-height: none;
	}
	.burgerWrapper {
		display: none;
	}
	.navigationContainer {
		grid-column: span 6 / span 6;
	}
	.navigations {
		flex-direction: row;
		justify-content: flex-end;
	}
	.navigation {
		padding-left: 1.75rem;
		padding-right: 1.75rem;
		width: auto;
	}
	.buttonWrapper {
		width: auto;
	}
}

