let scrollSnaps = []
let selectedIndex = 0

function onInit(e) {
	emblaApi = e.detail
	emblaApi.on('select', onSelect)
	scrollSnaps = emblaApi.scrollSnapList()
}

function scrollTo(index) {
	emblaApi.scrollTo(index)
}

function onSelect(e) {
	selectedIndex = e.selectedScrollSnap()
}

